package hyeon.day_03;

public class Test04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int k = 20;
		System.out.println(k);		
		k = k + 5;
		System.out.println(k);
		k = 300;
		System.out.println(k);
		
		final int TEST_DATA; //상수변수는 대분자로 표현한다.
		TEST_DATA = 55; //상수변수가 많을 수록 Program은 안정되어 있다고 볼 수 있다.
		System.out.write(TEST_DATA);
		System.out.write(TEST_DATA+1);
		System.out.write(TEST_DATA+2);
		System.out.write(TEST_DATA+3);
		System.out.write(TEST_DATA+4);
		System.out.flush();
		System.out.println();
		System.out.write(65);
		System.out.write(66);
		System.out.write(67);
		System.out.write(68);
		System.out.write(69);
		System.out.flush();
		System.out.println();
		
	}

}
